import React, { useEffect, useState } from "react";
import { useAuth } from "../context/AuthContext";
import axios from "axios";
import { API_URL } from "../config";

const Home = () => {
  const { user } = useAuth();
  const [posts, setPosts] = useState([]);
  const [formData, setFormData] = useState({
    game_type: "",
    region: "",
    game_mode: "",
  });

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get(`${API_URL}/posts`);
        setPosts(response.data);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };

    fetchPosts();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`${API_URL}/posts`, formData);
      setPosts((prev) => [...prev, response.data]); // Add new post to the list
      setFormData({ game_type: "", region: "", game_mode: "" }); // Reset form
    } catch (error) {
      console.error("Error creating post:", error);
    }
  };

  return (
    <div className="max-w-2xl mx-auto bg-gray-800 rounded-lg shadow-xl p-8">
      {user ? (
        <div>
          <h2 className="text-2xl font-semibold text-white mb-4">
            Create LFG Post
          </h2>
          <form onSubmit={handleSubmit} className="space-y-6">
            <div>
              <label className="block text-sm font-medium text-gray-300">
                Game Type
              </label>
              <select
                name="game_type"
                value={formData.game_type}
                onChange={handleInputChange}
                required
                className="mt-1 block w-full bg-gray-700 border border-gray-600 rounded-md py-2 px-3 text-white focus:outline-none focus:ring-2 focus:ring-purple-500"
              >
                <option value="">Select Game Type</option>
                <option value="The Finals">The Finals</option>
                <option value="Marvel Rivals">Marvel Rivals</option>
                <option value="Valorant">Valorant</option>
              </select>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-300">
                Region
              </label>
              <select
                name="region"
                value={formData.region}
                onChange={handleInputChange}
                required
                className="mt-1 block w-full bg-gray-700 border border-gray-600 rounded-md py-2 px-3 text-white focus:outline-none focus:ring-2 focus:ring-purple-500"
              >
                <option value="">Select Region</option>
                <option value="NA">NA</option>
                <option value="EU">EU</option>
                <option value="Asia">Asia</option>
                <option value="OCE">OCE</option>
              </select>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-300">
                Game Mode
              </label>
              <select
                name="game_mode"
                value={formData.game_mode}
                onChange={handleInputChange}
                required
                className="mt-1 block w-full bg-gray-700 border border-gray-600 rounded-md py-2 px-3 text-white focus:outline-none focus:ring-2 focus:ring-purple-500"
              >
                <option value="">Select Game Mode</option>
                <option value="Quick Match">Quick Match</option>
                <option value="Rank">Rank</option>
                <option value="Custom">Custom</option>
              </select>
            </div>
            <button
              type="submit"
              className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-purple-500"
            >
              Create LFG Post
            </button>
          </form>

          <h2 className="text-2xl font-semibold text-white mt-8 mb-4">
            Active LFG Posts
          </h2>
          <div className="space-y-4">
            {posts.length === 0 ? (
              <p className="text-gray-400">
                No active posts. Be the first to create one!
              </p>
            ) : (
              posts.map((post) => (
                <div
                  key={post.id}
                  className="bg-gray-800 rounded-lg p-4 border border-gray-700"
                >
                  <h3 className="text-purple-400 font-semibold">
                    {post.game_type}
                  </h3>
                  <p>Region: {post.region}</p>
                  <p>Mode: {post.game_mode}</p>
                  <p>Player: {user.displayName}</p>
                  <p>{new Date(post.created_at).toLocaleTimeString()}</p>
                </div>
              ))
            )}
          </div>
        </div>
      ) : (
        <p>Please log in to see your posts.</p>
      )}
    </div>
  );
};

export default Home;
