import React, { useEffect, useState } from "react";
import { useAuth } from "../context/AuthContext";
import axios from "axios";
import { API_URL } from "../config";
import DiscordLogin from "./DiscordLogin";
import CreatePostForm from "./CreatePostForm";

const Home = () => {
  const { user } = useAuth();
  const [posts, setPosts] = useState([]);
  const [formData, setFormData] = useState({
    game_type: "",
    region: "",
    game_mode: "",
  });
  const [currentPage, setCurrentPage] = useState(1);
  const postsPerPage = 4; // Adjust this number as needed
  const [showCreatePostForm, setShowCreatePostForm] = useState(false);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get(`${API_URL}/posts`);
        setPosts(response.data);
        console.log(response.data);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };

    fetchPosts();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`${API_URL}/posts`, formData);
      // Reset form
      setFormData({ game_type: "", region: "", game_mode: "" });
      // Fetch updated posts immediately after creating a new one
      const updatedResponse = await axios.get(`${API_URL}/posts`);
      setPosts(updatedResponse.data);
    } catch (error) {
      console.error("Error creating post:", error);
    }
  };

  const indexOfLastPost = currentPage * postsPerPage;
  const indexOfFirstPost = indexOfLastPost - postsPerPage;
  const currentPosts = posts.slice(indexOfFirstPost, indexOfLastPost);

  const totalPages = Math.ceil(posts.length / postsPerPage);

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const handlePrevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handleCreatePostClick = () => {
    setShowCreatePostForm(true);
  };

  return (
    <div className="max-w-2xl mx-auto bg-gray-800 rounded-lg shadow-xl p-8">
      {user ? (
        <div>
          <h2 className="text-2xl font-semibold text-white mb-4">
            Create LFG Post
          </h2>
          <button
            onClick={handleCreatePostClick}
            className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-purple-500"
          >
            Create LFG Post
          </button>

          {showCreatePostForm && <CreatePostForm />}

          <h2 className="text-2xl font-semibold text-white mt-8 mb-4">
            Active LFG Posts
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {currentPosts.length === 0 ? (
              <p className="text-gray-400">
                No active posts. Be the first to create one!
              </p>
            ) : (
              currentPosts.map((post) => (
                <div
                  key={post.id}
                  className="bg-gray-800 rounded-lg p-4 border border-gray-700 hover:border-purple-500 transition-colors"
                >
                  <h3 className="text-purple-400 font-semibold text-xl mb-2">
                    {post.game_type}
                  </h3>
                  <div className="text-gray-300">
                    <div className="flex items-center space-x-2">
                      <span className="text-gray-400">Region:</span>
                      <span>{post.region}</span>
                    </div>
                    <div className="mt-1">
                      <span className="text-gray-400">Mode:</span>
                      <span className="ml-2">{post.game_mode}</span>
                    </div>
                    <div className="mt-1">
                      <span className="text-gray-400">Player:</span>
                      <span className="ml-2">{user.displayName}</span>
                    </div>
                    <div className="mt-1 text-gray-400">
                      <span className="text-gray-400">Created:</span>
                      <span className="ml-2">
                        {post.created_at
                          ? new Date(post.created_at.replace(" ", "T"))
                              .toLocaleString("en-GB", {
                                day: "2-digit",
                                month: "2-digit",
                                year: "2-digit",
                                hour: "2-digit",
                                minute: "2-digit",
                                hour12: false,
                              })
                              .replace(",", "")
                          : "Invalid Date"}
                      </span>
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>

          <div className="flex justify-between mt-4">
            <button
              onClick={handlePrevPage}
              disabled={currentPage === 1}
              className="bg-gray-600 text-white px-4 py-2 rounded disabled:opacity-50"
            >
              Previous
            </button>
            <button
              onClick={handleNextPage}
              disabled={currentPage === totalPages}
              className="bg-gray-600 text-white px-4 py-2 rounded disabled:opacity-50"
            >
              Next
            </button>
          </div>
        </div>
      ) : (
        <DiscordLogin />
      )}
    </div>
  );
};

export default Home;
