import React, { useState, useEffect } from "react";
import axios from "axios";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import Registration from "./components/Registration";
import { API_URL } from "./config";
import { useAuth } from "./context/AuthContext";
import Update from "./components/Update";
import Home from "./components/Home";

function App() {
  const { user, logout } = useAuth();
  const [posts, setPosts] = useState([]);
  const [formData, setFormData] = useState({
    game_type: "",
    region: "",
    game_mode: "",
  });

  const gameTypes = ["The Finals", "Marvel Rivals", "Valorant"];
  const regions = ["NA", "EU", "Asia", "OCE"];

  // Game modes mapping object
  const gameModesByGame = {
    "The Finals": {
      Cashout: ["Quick Cash", "Ranked Tournament"],
      "Bank It": ["Bank It"],
      "Terminal Attack": ["Terminal Attack"],
    },
    Valorant: [
      "Unrated",
      "Competitive",
      "Spike Rush",
      "Deathmatch",
      "Escalation",
      "Replication",
      "Snowball Fight",
      "Swiftplay",
    ],
    "Marvel Rivals": ["Quick Match", "Rank", "Custom"],
  };

  // Get available game modes based on selected game
  const getGameModes = () => {
    const selectedGame = formData.game_type;
    if (!selectedGame) return [];

    if (selectedGame === "The Finals") {
      // For The Finals, we need to handle the Cashout submodes
      const modes = [];
      Object.entries(gameModesByGame[selectedGame]).forEach(
        ([mode, submodes]) => {
          if (Array.isArray(submodes)) {
            if (mode === "Cashout") {
              submodes.forEach((submode) => modes.push(`Cashout - ${submode}`));
            } else {
              modes.push(...submodes);
            }
          }
        }
      );
      return modes;
    }

    return gameModesByGame[selectedGame];
  };

  useEffect(() => {
    fetchPosts();
    const interval = setInterval(fetchPosts, 30000); // Refresh every 30 seconds
    return () => clearInterval(interval);
  }, []);

  const fetchPosts = async () => {
    try {
      const response = await axios.get(`${API_URL}/posts`);
      setPosts(response.data);
    } catch (error) {
      console.error("Error fetching posts:", error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`${API_URL}/posts`, formData);
      setFormData({ game_type: "", region: "", game_mode: "" });
      fetchPosts();
    } catch (error) {
      console.error("Error creating post:", error);
      if (error.response) {
        console.error("Response error:", error.response.data);
      } else if (error.request) {
        console.error("Request error:", error.request);
      }
      alert("Failed to create post. Please try again.");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
      // Reset game_mode when game_type changes
      ...(name === "game_type" ? { game_mode: "" } : {}),
    }));
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/register" element={<Registration />} />
        <Route path="/update" element={<Update />} />
        <Route
          path="/"
          element={
            <div className="min-h-screen bg-gradient-to-br from-gray-900 to-gray-800 text-white">
              <div className="container mx-auto px-4 py-8">
                {/* Header */}
                <div className="text-center mb-12">
                  <h1 className="text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-purple-400 to-pink-600">
                    LFG Finder
                  </h1>
                  {user ? (
                    <div>
                      <p className="text-gray-200 mt-2">
                        Welcome,{" "}
                        <span className="text-purple-400">
                          {user.displayName}
                        </span>
                        !
                      </p>
                      <button
                        onClick={() => {
                          logout();
                          window.location.reload(); // Refresh the page after logout
                        }}
                        className="mt-4 px-4 py-2 text-sm font-medium text-white bg-red-600 rounded-md hover:bg-red-700 transition-colors"
                      >
                        Logout
                      </button>
                    </div>
                  ) : (
                    <p className="text-gray-400 mt-2">
                      Find your perfect gaming squad
                    </p>
                  )}
                  <div className="mt-4">
                    {!user && (
                      <Link
                        to="/register"
                        className="inline-block px-6 py-2 text-sm font-medium text-white bg-purple-600 rounded-md hover:bg-purple-700 transition-colors"
                      >
                        Register Now
                      </Link>
                    )}
                  </div>
                </div>

                <div className="grid md:grid-cols-2 gap-8">
                  {/* Create Post Form */}
                  <div className="bg-gray-800 rounded-lg p-6 shadow-lg">
                    <h2 className="text-xl font-semibold mb-6 text-purple-400">
                      Create LFG Post
                    </h2>
                    <form onSubmit={handleSubmit} className="space-y-6">
                      <div>
                        <label className="block text-sm font-medium text-gray-300 mb-2">
                          Game Type
                        </label>
                        <select
                          name="game_type"
                          value={formData.game_type}
                          onChange={handleChange}
                          className="w-full bg-gray-700 border border-gray-600 rounded-md py-2 px-3 text-white focus:outline-none focus:ring-2 focus:ring-purple-500"
                          required
                        >
                          <option value="">Select Game Type</option>
                          {gameTypes.map((type) => (
                            <option key={type} value={type}>
                              {type}
                            </option>
                          ))}
                        </select>
                      </div>

                      <div>
                        <label className="block text-sm font-medium text-gray-300 mb-2">
                          Region
                        </label>
                        <select
                          name="region"
                          value={formData.region}
                          onChange={handleChange}
                          className="w-full bg-gray-700 border border-gray-600 rounded-md py-2 px-3 text-white focus:outline-none focus:ring-2 focus:ring-purple-500"
                          required
                        >
                          <option value="">Select Region</option>
                          {regions.map((region) => (
                            <option key={region} value={region}>
                              {region}
                            </option>
                          ))}
                        </select>
                      </div>

                      <div>
                        <label className="block text-sm font-medium text-gray-300 mb-2">
                          Game Mode
                        </label>
                        <select
                          name="game_mode"
                          value={formData.game_mode}
                          onChange={handleChange}
                          className="w-full bg-gray-700 border border-gray-600 rounded-md py-2 px-3 text-white focus:outline-none focus:ring-2 focus:ring-purple-500"
                          required
                          disabled={!formData.game_type}
                        >
                          <option value="">Select Game Mode</option>
                          {getGameModes().map((mode) => (
                            <option key={mode} value={mode}>
                              {mode}
                            </option>
                          ))}
                        </select>
                      </div>

                      <button
                        type="submit"
                        className="w-full bg-gradient-to-r from-purple-500 to-pink-500 text-white py-2 px-4 rounded-md hover:from-purple-600 hover:to-pink-600 transition duration-300 ease-in-out transform hover:-translate-y-1"
                      >
                        Create LFG Post
                      </button>
                    </form>
                  </div>

                  {/* Active Posts */}
                  <div>
                    <h2 className="text-xl font-semibold mb-6 text-purple-400">
                      Active LFG Posts
                    </h2>
                    <div className="space-y-4 max-h-[600px] overflow-y-auto pr-2">
                      {posts.length === 0 ? (
                        <div className="text-center text-gray-400 py-8">
                          No active posts. Be the first to create one!
                        </div>
                      ) : (
                        posts.map((post) => (
                          <div
                            key={post.id}
                            className="bg-gray-800 rounded-lg p-4 border border-gray-700 hover:border-purple-500 transition-colors duration-300"
                          >
                            <div className="flex items-center justify-between mb-2">
                              <span className="text-purple-400 font-semibold">
                                {post.game_type}
                              </span>
                              <span className="text-sm text-gray-400">
                                {new Date(post.created_at).toLocaleTimeString()}
                              </span>
                            </div>
                            <div className="flex gap-4 text-sm text-gray-300">
                              <div className="flex items-center gap-1">
                                <span className="text-gray-400">Region:</span>
                                {post.region}
                              </div>
                              <div className="flex items-center gap-1">
                                <span className="text-gray-400">Mode:</span>
                                {post.game_mode}
                              </div>
                            </div>
                          </div>
                        ))
                      )}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
