import React from "react";
import { useNavigate, Outlet } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

const Layout = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleUpdateClick = () => {
    navigate("/update");
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 to-gray-800 text-white">
      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-purple-400 to-pink-600">
            LFG Finder
          </h1>
          {user ? (
            <div>
              <p className="text-gray-200 mt-2">
                Welcome,{" "}
                <span className="text-purple-400">{user.display_name}</span>!
              </p>
              <div className="flex justify-center space-x-4 mb-4 mt-4">
                <button
                  onClick={handleUpdateClick}
                  className="text-white bg-blue-500 hover:bg-blue-700 px-4 py-2 rounded"
                >
                  Update My Info
                </button>
                <button
                  onClick={() => {
                    logout();
                    window.location.reload();
                  }}
                  className="text-white bg-red-600 hover:bg-red-700 px-4 py-2 rounded"
                >
                  Logout
                </button>
              </div>
            </div>
          ) : (
            <p className="text-gray-400 mt-2">Find your perfect gaming squad</p>
          )}
        </div>

        {/* Main Content */}
        <Outlet />
      </div>
    </div>
  );
};

export default Layout;
