import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import axios from "axios";
import { API_URL } from "../config";

const Home = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get(`${API_URL}/posts`);
        setPosts(response.data);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };

    fetchPosts();
  }, []);

  const handleUpdateClick = () => {
    navigate("/update");
  };

  return (
    <div>
      <h1>Welcome to LFG Finder</h1>
      {user ? (
        <div>
          <h2>Active LFG Posts</h2>
          <div className="space-y-4">
            {posts.length === 0 ? (
              <p>No active posts. Be the first to create one!</p>
            ) : (
              posts.map((post) => (
                <div
                  key={post.id}
                  className="bg-gray-800 rounded-lg p-4 border border-gray-700"
                >
                  <h3 className="text-purple-400 font-semibold">
                    {post.game_type}
                  </h3>
                  <p>Region: {post.region}</p>
                  <p>Mode: {post.game_mode}</p>
                  <p>Player: {user.displayName}</p>
                  <p>{new Date(post.created_at).toLocaleTimeString()}</p>
                </div>
              ))
            )}
          </div>
          <button onClick={handleUpdateClick}>Go to Update</button>
        </div>
      ) : (
        <p>Please log in to see your posts.</p>
      )}
    </div>
  );
};

export default Home;
